import React from 'react'
import axios from 'axios';
import { useEffect, useState } from "react";
function Home() {
    const [listOfPosts, setListOfPosts] = useState([]);
    useEffect(() => {
        axios.get("http://localhost:3001/posts").then((response) => {
            console.log(response.data);
            setListOfPosts(response.data);
            setUpEventListener();
        });
    }, []);
    return (
        <div className='posts'>

            {listOfPosts.map((value, key) => {
                var html;
                html=<div className="post" id={key}><div className="title">{value.title}</div><div className="body">{value.postText}</div><div className="footer">{value.userName}</div></div>;
                return (
                    html
                );
            })}
        </div>
    )
}
var setUpEventListener = () => {
    console.log(document.getElementById(10).textContent);
    document.querySelector('.posts').addEventListener('click', (event) => {
        console.log(event);
    })

}
export default Home
