import * as Yup from "yup";
function createPost() {
    const initialValues={
        title:"",
        postTest:"",
        userName:""
    };
    const onSubmit=(data)=>{
        console.log(data);
    }
    return (
        <div className="createPostPage">
            <Formik initialValues={initialValues} onSubmit={onSubmit}  >
                <Form className="formContainer">
                    <label>Title :</label>
                    <Field id="inputCreatePost" name ="title" placeholder="Ex.titile..."/>
                    <label>Post :</label>
                    <Field id="inputCreatePost" name ="title" placeholder="Ex.News..."/>
                    <label>userName :</label>
                    <Field id="inputCreatePost" name ="userNmae" placeholder="Ex.Yaregal..."/>
                    <button type='submit'>Create Post</button>
                </Form>
            </Formik>
        </div>
    )
}

export default createPost
